<?php declare(strict_types = 1);

namespace Apitte\OpenApi\Tracy;

use Apitte\OpenApi\ISchemaBuilder;
use Tracy\IBarPanel;

class SwaggerUIPanel implements IBarPanel
{

	public const
		EXPANSION_FULL = 'full',
		EXPANSION_LIST = 'list',
		EXPANSION_NONE = 'none';

	public const EXPANSIONS = [
		self::EXPANSION_FULL,
		self::EXPANSION_LIST,
		self::EXPANSION_NONE,
	];

	private ?string $url = null;

	private string $expansion = self::EXPANSION_LIST;

	/** @var string|bool */
	private $filter;

	private string $title = 'OpenAPI';

	private ISchemaBuilder $schemaBuilder;

	public function __construct(ISchemaBuilder $schemaBuilder)
	{
		$this->schemaBuilder = $schemaBuilder;
	}

	public function setUrl(?string $url): void
	{
		$this->url = $url;
	}

	public function setExpansion(string $expansion): void
	{
		$this->expansion = $expansion;
	}

	/**
	 * @param string|bool $filter
	 */
	public function setFilter($filter): void
	{
		$this->filter = $filter;
	}

	public function setTitle(string $title): void
	{
		$this->title = $title;
	}

	/**
	 * Renders HTML code for custom tab.
	 */
	public function getTab(): string
	{
		return '<span title="Swagger UI for Open Api Specification"><img style="height: 16px; width: auto; display: inline;" src="data:image/png;base64,"/> ' . $this->title . ' </span>';
	}

	/**
	 * Renders HTML code for custom panel.
	 */
	public function getPanel(): string
	{
		ob_start();
		// @codingStandardsIgnoreStart
		$spec = $this->schemaBuilder->build()->toArray();
		$url = $this->url;
		$expansion = $this->expansion;
		$filter = $this->filter;
		// @codingStandardsIgnoreEnd
		require __DIR__ . '/templates/panel.phtml';

		return (string) ob_get_clean();
	}

}
